CREATE TABLE IF NOT EXISTS "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);


CREATE TABLE IF NOT EXISTS "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(150) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "auth_user" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "username" varchar(150) NOT NULL UNIQUE, "last_name" varchar(150) NOT NULL, "email" varchar(254) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL, "first_name" varchar(150) NOT NULL);
CREATE TABLE IF NOT EXISTS "blog_chatuser" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_name" varchar(200) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "blog_message" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "message" varchar(250) NOT NULL UNIQUE, "pub_date" date NOT NULL, "username_id" integer NOT NULL REFERENCES "blog_chatuser" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "blog_room" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "room_name" varchar(200) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "blog_room_messages" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "room_id" integer NOT NULL REFERENCES "blog_room" ("id") DEFERRABLE INITIALLY DEFERRED, "message_id" integer NOT NULL REFERENCES "blog_message" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "blog_room_users" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "room_id" integer NOT NULL REFERENCES "blog_room" ("id") DEFERRABLE INITIALLY DEFERRED, "chatuser_id" integer NOT NULL REFERENCES "blog_chatuser" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);


